#include <stdio.h>
#include <stdlib.h>
#include <string.h>
int main()
{
    char num [1000] = "";
    int contador, x;
    char p0, p1, p2, p3, p4;
    p0 = (num[0]);
    p1 = (num[1]);
    p2 = (num[2]);
    p3 = (num[3]);
    p4 = (num[4]);
    x = p0 * p1 * p2 * p3 * p4;
    for(contador = 0; num[contador] != '\0'; contador++){
        if (num[contador] != '\0' && num[contador+1] != '\0' && num[contador+2] != '\0' && num[contador+3] != '\0' && num[contador+4] != '\0'){
            if (num[contador] == '0' || num[contador+1] == '0' || num[contador+2] == '0' || num[contador+3] == '0' || num[contador+4] == '0'){
                contador = contador + 5;
            }
        }
        if (num[contador] * num[contador+1] * num[contador+2] * num[contador+3] * num[contador+4] > x){
            x = (p0) * (p1) * (p2) * (p3) * (p4);
            p0 = (num[contador]);
            p1 = (num[contador+1]);
            p2 = (num[contador+2]);
            p3 = (num[contador+3]);
            p4 = (num[contador+4]);
        }
    }
    
    printf("Os numeros em sequencia que gerariam o maior prouto eh: %c%c%c%c%c\n", p0, p1, p2, p3, p4);
    
    system("pause");
    return 0;
}
